mapper.update:

UPDATE SLIP
SET SLIP_STATUS=#{slipStatus}
WHERE SLIP_NO = #{slipNo}


UPDATE SLIP
SET APPROVAL_DATE=#{approvalDate},
    SLIP_STATUS=#{slipStatus},
    APPROVAL_EMP_CODE=#{approvalEmpCode}
WHERE SLIP_NO = #{slipNo}

mapper.select:
SELECT s.SLIP_NO as slipNo, s.REPORTING_EMP_CODE as reportingEmpCode, s.APPROVAL_EMP_CODE as approvalEmpCode, s.EXPENSE_REPORT as expenseReport, s.SLIP_TYPE as slipType, s.SLIP_STATUS as slipStatus, s.APPROVAL_DATE as approvalDate, s.REPORTING_DATE as reportingDate, s.DEPT_CODE as deptCode, s.ACCOUNT_PERIOD_NO as accountPeriodNo FROM slip s WHERE s.REPORTING_DATE BETWEEN #{fromDate} AND #{toDate}
AND s.SLIP_STATUS = #{slipStatus}
ORDER BY s.REPORTING_DATE
